import { useState } from "react";
import "./App.css";
import { useEffect } from "react";

//kartların tanımlanması
const cardList = [
  { path: "/img/1.jpeg" },
  { path: "/img/2.jpeg" },
  { path: "/img/3.jpeg" },
  { path: "/img/4.jpeg" },
  { path: "/img/5.jpeg" },
  { path: "/img/6.jpeg" },
];
function App() {
  const [cards, setCards] = useState([]);

  const prepareCard = () => {
    const sortedCards = [...cardList, ...cardList] //her karttan 2 adet
      .sort(() => 0.5 - Math.random()) //random'un otomatik olarak 0-1 arası değer üretmesi
      .map((card) => ({ ...card, id: Math.random() })); //kartların random sıralanması

    setCards(sortedCards);
  };

  useEffect(() => {
    prepareCard();
  }, []);

  return (
    <div className="container">
      <h1>Memory Game App</h1>
      <button className="" onClick={prepareCard}>Oyunu Başlat</button>
      <div className="card-grid">
        {cards.map((card) => (
          <div className="card">
            {/* kartların ön ve arka yüzü */}
            <img className="cardFront" src={card.path} alt="" />
            <img className="cardBack" src="/img/cover.jpeg" alt="" />
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
